import axios from "axios"
import { PRODUCT_DETAILS_FAIL, PRODUCT_DETAILS_REQUEST, PRODUCT_DETAILS_SUCCESS, PRODUCT_LIST_FAIL, PRODUCT_LIST_REQUEST, PRODUCT_LIST_SUCCESS } from "../constants/productConstants"

const listProducts = ()=> async (dispatch)=>{
    try{
        dispatch({type: PRODUCT_LIST_REQUEST})
        //const {data} = await axios.get("/api/products");
        const data=[{"_id":"1","name":"Slim Shirt","catagory":"Shirts","image":"/images/shirtb.png","price":60,"brand":"Nike","rating":4.5,"numReviews":10},{"_id":"2","name":"Fit Shirt","catagory":"Shirts","image":"/images/shirtb.png","price":50,"brand":"Nike","rating":4.2,"numReviews":5},{"_id":"3","name":"Best Pants","catagory":"Pants","image":"/images/shirtb.png","price":70,"brand":"Nike","rating":4.5,"numReviews":8},{"_id":"4","name":"Best Pants","catagory":"Pants","image":"/images/shirtb.png","price":70,"brand":"Nike","rating":4.5,"numReviews":8},{"_id":"5","name":"Best Pants","catagory":"Pants","image":"/images/shirtb.png","price":70,"brand":"Nike","rating":4.5,"numReviews":8}]
        dispatch({type: PRODUCT_LIST_SUCCESS, payload: data})

    }
    catch(error){
        dispatch({type: PRODUCT_LIST_FAIL, payload: error.message})

    }

}
const detailsProduct = (productId) => async (dispatch)=> {
    try {
        dispatch({type:PRODUCT_DETAILS_REQUEST, payload:productId});
        const {data} = await axios.get("http://localhost:5000/api/products");
        dispatch({type:PRODUCT_DETAILS_SUCCESS, payload: data[0]});

    } catch (error) {
        dispatch({type:PRODUCT_DETAILS_FAIL, payload: error.message});

    }
}
export {listProducts, detailsProduct}
